<script>    $(document).ready(function(){        $("#password").val("");    });    function guardar(x)    {        var pass = $("#password").val();        if(pass=="")            return swal.fire("Atencion","No ha ingresado una contraseÃ±a","error");        if(!validar())            return;        $(x).start();        $.post("{{url('/usuarios/store')}}", {            _token: $('meta[name=csrf-token]').attr('content'),            name: $('#name').val(),            password: pass,            email: $('#email').val(),            rol: $("#rol").val()        })        .done(function (data) {            $(x).stop();            Flash(data.message,data.status);            if(data.status=="success")                window.location.replace("{!! url('/usuarios') !!}");        })         .fail(function(xhr, textStatus, errorThrown)           {            $(x).stop();               errorShow(xhr.responseText);           });    }    function actualizar(x)    {        if(!validar())            return;        $(x).start();        $.post("{{url('/usuarios/update')}}", {            _token: $('meta[name=csrf-token]').attr('content'),            @if(isset($usuario))            id: "{!! $usuario->id !!}",            @endif            name: $('#name').val(),            password: $("#password").val(),            email: $('#email').val(),            rol: $("#rol").val()        })        .done(function (data) {            Flash(data.message,data.status);            if(data.status=="success")                window.location.replace("{!! url('/usuarios') !!}");        })        .fail(function(xhr, textStatus, errorThrown)         {            $(x).stop();             errorShow(xhr.responseText);         });    }</script>